export const getTask = (taskIdentifier) => async (dispatch) => {
  try {
    const res = await axios.get(`/api/task/${taskIdentifier}`);
    dispatch({
      type: GET_TASK,
      payload: res.data,
    });
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
    //  window.location.href = "/dashboard";
  }
};

